addHook("MobjDeath", function(target, inflictor, source)
  if not target.player then
    return false
  end
  if inflictor then
    source = inflictor.target
  end
  local v = P_RandomKey(#DieVerbs)
  local n = P_RandomKey(#DieNouns)
  if G_RingSlingerGametype()
  and source and source.player then
    -- Death notices in Match will accompany the originals, so you can see who really shot whom.
    for reader in players.iterate do
      if reader.obituaries then
	if v % 10 == 0 then
	  CONS_Printf(reader, target.player.name..DieVerbs[v].." It was all "..source.player.name.."'s fault.")
	else
	  CONS_Printf(reader, target.player.name..DieVerbs[v].." "..source.player.name..DieNouns[(n/10)*10])
	end
      end
    end
  else
    -- Co-op doesn't have death notices, so the custom notice stands alone.
    for reader in players.iterate do
      if reader.obituaries then
	if v % 10 == 0 then
	  CONS_Printf(reader, target.player.name..DieVerbs[v])
	elseif n % 10 == 0 then
	  local d = INT32_MAX
	  local p = target.player
	  for bystander in players.iterate do
	    local r = R_PointToDist2(target.x, target.y, bystander.mo.x, bystander.mo.y)
	    if bystander ~= target.player and r < d then
	      d = r
	      p = bystander
	    end
	  end
	  CONS_Printf(reader, target.player.name..DieVerbs[v].." "..p.name..DieNouns[n])
	else
	  CONS_Printf(reader, target.player.name..DieVerbs[v]..DieNouns[n])
	end
      end
    end
  end
  return false
end, MT_PLAYER)


COM_AddCommand("obituaries", function(player)
  if player.obituaries then -- Some people just don't like fun, do they? (don't say "conflicts" you coward. >R)
    player.obituaries = false
    CONS_Printf(player, "Obituaries are disabled. Why did you add these if you disabled them?")
  else -- But some people do :) >R
    player.obituaries = true
    CONS_Printf(player, "Obituaries are enabled. Enjoy all "..(#DieVerbs)*(#DieNouns).." possible death notices!")
  end
end)

COM_AddCommand("countobits", function(player)
  -- Pulling out the counter function and making it a bit more detailed. Wow, luascript good moment?! >R
  CONS_Printf(player, "There are "..(#DieVerbs)*(#DieNouns).." possible death notices - "..(#DieVerbs).." verbs and "..(#DieNouns).." nouns specifically.")
end)

addHook("ThinkFrame", function()
  for player in players.iterate do
    if player.obituaries == nil then
      player.obituaries = true
    end
  end
end)